# tool_debug_zalo.py
import json
import hashlib
import base64
from Crypto.Cipher import AES
from Crypto.Util.Padding import pad, unpad

class ZaloDebug:
    # Key cứng (AES-256 / 32 bytes UTF-8)
    ZCID_STATIC_KEY = b"3FC4F0D2AB50057BCE0D90D9187A22B1"

    @staticmethod
    def _derive_encrypt_key_string(zcid: str, zcid_ext: str) -> str:
        # 1. MD5 zcid_ext
        md5_hash = hashlib.md5(zcid_ext.encode()).hexdigest().upper()
        
        # 2. Tách chẵn lẻ
        md5_even = md5_hash[0::2]
        zcid_even = zcid[0::2]
        zcid_odd = zcid[1::2]
        
        # 3. Ghép key
        part1 = md5_even[:8]
        part2 = zcid_even[:12]
        part3 = zcid_odd[::-1][:12] 
        return part1 + part2 + part3

    @staticmethod
    def decrypt_response(encrypted_b64: str, zcid: str, zcid_ext: str):
        try:
            print(f"\n--- BẮT ĐẦU GIẢI MÃ ---")
            print(f"INPUT ZCID: {zcid}")
            print(f"INPUT ZCID_EXT: {zcid_ext}")
            
            # 1. Derive Key
            key_str = ZaloDebug._derive_encrypt_key_string(zcid, zcid_ext)
            print(f"KEY (Derived): {key_str}")
            
            # 2. Setup AES-256
            key_bytes = key_str.encode('utf-8')
            iv = b'\x00' * 16
            cipher = AES.new(key_bytes, AES.MODE_CBC, iv=iv)
            
            # 3. Decrypt
            encrypted_data = base64.b64decode(encrypted_b64)
            decrypted = cipher.decrypt(encrypted_data)
            unpadded = unpad(decrypted, 16)
            
            return json.loads(unpadded.decode('utf-8'))
        except Exception as e:
            print(f"❌ LỖI: {e}")
            return None

